package CodingTest;

public class Ex2_0830 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j =0; j<5;j++) {
		    for(int i = 0; i <= j; i++) {
			System.out.print("*");
		    }
		  System.out.println();
		}
		for(int j =5; j>0; j--) {
			  for(int i=0 ; i < j; i++) {
				System.out.print("*");
			  }
			  System.out.println();
			}

	}

}	
